import React from "react";
import styled from "styled-components";

const Main = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Div = styled.div`
  > input {
    display: block;
    margin-bottom: 15px;
    font-size: 18px;
  }
  > input:focus {
    outline: none;
  }

  > h3 {
    text-align: center;
    margin-bottom: 15px;
  }

  > button {
    background: transparent;
    border: 1px solid #000;
    border-radius: 8px;
    padding: 6px 15px;
    text-transform: uppercase;
    display: block;
    margin: 0 auto;
    cursor: pointer;
    margin-top: 15px;
  }
`;

const Home = ({ addUserLocal, users }) => {
  return (
    <Main>
      <Div>
        <h3>User Details</h3>

<label>Name</label>
        <input type="text" value={users.first_name} />

        <button onClick={() => addUserLocal({})}>log out</button>
      </Div>
    </Main>
  );
};

export default Home;
