import React from "react";
import styled from "styled-components";

const Main = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Div = styled.div`
  ${
    "" /* > input {
    display: block;
    margin-bottom: 15px;
    font-size: 18px;
  }
  > input:focus {
    outline: none;
  } */
  }

  > h3 {
    text-align: center;
    margin-bottom: 15px;
  }

  >h5 >span{
    width: 110px !important;
    display: inline-table;
  }

  > button {
    background: transparent;
    border: 1px solid #000;
    border-radius: 8px;
    padding: 6px 15px;
    text-transform: uppercase;
    display: block;
    margin: 0 auto;
    cursor: pointer;
    margin-top: 15px;
  }
`;

const Home = ({ addUserLocal, users }) => {
  return (
    <Main>
      <Div>
        <h3>User Details</h3>

        <h5> <span>Name:</span> {users.first_name} {users.last_name} </h5>
        <h5> <span>Email Address:</span> {users.email} </h5>
        <h5> <span>Phone Number:</span> {users.phone_no} </h5>
        <h5> <span>Birth Date:</span> {users.dob} </h5>
        <h5> <span>Address:</span> {users.address} </h5>
        <h5> <span>Pin Code:</span> {users.pincode} </h5>
        <h5> <span>City:</span> {users.city} </h5>
        <h5> <span>State:</span> {users.state} </h5>

        <button onClick={() => addUserLocal({})}>log out</button>
      </Div>
    </Main>
  );
};

export default Home;
